import Vue from "vue";
import Router from "vue-router";
import Login from "./components/user/Login.vue";
import Regist from "./components/user/Regist.vue";
import Home from "./components/Home.vue";
import Index from "./components/manager/Index.vue";
import axios from "axios";
import store from "./store/store";

Vue.use(Router);

const router = new Router();

router.options.routes = [
  {
    path: "/",
    redirect: "/home",
  },
  {
    path: "/login",
    component: Login,
  },
  {
    path: "/regist",
    component: Regist,
  },
  {
    path: "/home",
    component: Home,
  },
  {
    path: "/index",
    name: "index",
    redirect: "/index/Profile",
    component: Index,
    // 路由嵌套
    children: [
      {
        path: "/index/profile",
        component: () => import("./components/manager/Profile.vue"),
      },
      {
        path: "/index/collection",
        component: () => import("./components/manager/Collection.vue"),
      },
      {
        path: "/index/share",
        component: () => import("./components/manager/Share.vue"),
      },
    ],
  },
];

let oRoutes = router.options.routes;
const buildRouter = () => {
  let data = store.state.menu_data;
  data.forEach((item) => {
    let new_router = {
      path: item.path,
      component: () => import("./components/" + item.component + ".vue"),
    };
    oRoutes[0].children.push(new_router);
  });
};

router.beforeEach((to, from, next) => {
  /* 判断路由是否已经加载过 */
  let isLoadRoute = store.state.isLoadRoute;
  if (!isLoadRoute) {
    // axios.get("/menuList").then((res) => {
    //   store.commit("setMeunData", res.data, menu_data);
    //   // 动态创建路由
    //   buildRouter();
    //   // 设置已经加载过的标记
    //   store.commit("setLoadRoute", true);
    // });
  }
  next();
});

export default router
