import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);
const state = {
  username: "aris",
  userState: 0,
};

const mutations = {
  setUser(state, name) {
    state.username = name;
  },
  setUserState(state, data) {
    state.userState += data;
  },
};

const getters = {
    getUserState (state) {
        let data
        if (state,userState == 0) {
            data = '无效'
        }
    }
}

export default new Vuex.Store({
  state,
  mutations,
});
