import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

// 定义会话状态
const state = { 
  // 菜单导航
  menu_data: [],
  //  加载动态路由标志
  isLoadRoute: false,
}; 

// 定义行为方法
const mutations = {
  
  //  设置菜单导航
  setMeunData(state, data) {
    state.menu_data = data;
  },
  //   设置加载路由标志
  setLoadRoute(state, data) {
    state.isLoadRoute = data;
  },
};

// 定义获取加工方法
const getters = { 
};

export default new Vuex.Store({
  userinfo,
  state,
  mutations,
  getters,
});
