import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

// 定义会话状态
const state = {
  // access_token
  token: "",
  // 用户信息
  userinfo: {
    user_id: 0,
    user_name: "",
    user_level: 0,
  },
//   菜单
  menu_data: [],
  isLoadRoute: false,
};

const mutations = {
  setUserInfo(state, data) {
    state.userinfo = JSON.parse(data);
  },
  setMeunData(state, data) {
    state.menu_data = data;
  },
  setLoadRoute(state, data) {
    state.isLoadRoute = data;
  },
  clearState(state) {
    state = {
      token: "",
      userinfo: {
        user_id: 0,
        user_name: "",
        user_level: 0,
      },
      menu_data: [],
      isLoadRoute: false,
    };
  },
};

const getters = {
  getUserState(state) {
    return state.token != "";
  },
  getUserLevel(state) {
    let data;
    if (state.userState == 0) {
      data = "用户";
    } else {
      data = state.userState + "管理员";
    }
    return data;
  },
  getToken(state) {
    return state.token;
  },
};

export default new Vuex.Store({
  state,
  mutations,
  getters,
});
