import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);
const state = {
  username: "aris",
};

const mutations = {
  setUser(state, name) {
    state.username = name;
  },
};

export default new Vuex.Store({
  state,
  mutations,
});
