import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);
const state = {
  username: "aris",
  userState: 0,
};

const mutations = {
  setUser(state, name) {
    state.username = name;
  },
  setUserState(state, data) {
    state.userState += data;
  },
};

const getters = {
  getUserState() {
    let data;
    if ((state, userState == 0)) {
      data = "无效";
    } else {
      data = state.userState + "级";
    }
    return data;
  },
};

export default new Vuex.Store({
  state,
  mutations,
  getters,
});
